import React, { Component } from 'react';

class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'macaxi',
    };
  }

  handleChange({ keyCode }) {
    console.log(keyCode);
  }

  render() {
    const { value } = this.state;
    return (
      <div className="input-container">
        <input type="text" value={value} onKeyDown={this.handleChange} placeholder="Digite um to-do" />
      </div>
    );
  }
}

export default Input;
