import React, { Component } from 'react';

class Input extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
    };
  }

  render() {
    const { value } = this.state;
    return (
      <div className="input-container">
        <input type="text" value="" placeholder="Digite um to-do" />
      </div>
    );
  }
}

export default Input;
