import React, { Component } from 'react';

class Input extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      value: '',
    };
  }

  handleChange({ key }) {
    const { state } = this.state;
    this.setState({
      value: value + key,
    });
    // console.log(key);
  }

  render() {
    const { value } = this.state;
    return (
      <div className="input-container">
        <input type="text" value={value} onKeyPress={this.handleChange} placeholder="Digite um to-do" />
      </div>
    );
  }
}

export default Input;
